import React, {useState} from "react";

const Restaurant = () => {



  return (
    <div>
      <h3>Restaurant Orders</h3>
    <ul>
      <Orders />
    </ul>
    </div>
  );
};
const RestaurantButton = (props) => {
  const { Restaurant } = props;
  return <button className="btn btn-primary" onClick={Restaurant}> Add </button>;
};
const Orders = () =>{
  const [orders, setOrders] = useState(0);
  const OrderOne = () => {
    return setOrders(orders + 1);
  };
  return(
    <li>
        Pizzas:{orders} 
        <RestaurantButton Restaurant={OrderOne} />
      </li>
  )
}
export default Restaurant;
