import React from 'react'
import FakeBookings from './data/fakeBookings.json'
const NumberOfBookedNights = () => {
  const inDate = FakeBookings.map((props) => (props.checkInDate))
  const outDate = FakeBookings.map((props) =>(props.checkOutDate))
  const dateIn = new Date(inDate)
  const dateOut = new Date(outDate)
  const DifferenceInTimes = dateOut.getTime() - dateIn.getTime()
  const DifferenceInDays = DifferenceInTimes / (1000 * 3600 * 24);
  return <div>
    {DifferenceInDays}
  </div>
}
export default NumberOfBookedNights;
