import React, {useState, useEffect} from 'react'
import FakeBookings from './data/fakeBookings.json'
import moment from 'moment'
import CustomerProfile from './CustomerProfile';


const SearchResults= ({results, id}) => {
  console.log(" This is the results",results);
  const [rowClass, setRowClass] = useState(false)
  const ToggleRow = () => {
    setRowClass(!rowClass)
  } 
  const dateDiffs = (checkInDate, checkOutDate) => {
    const inDate = moment(checkInDate)
    const outDate = moment(checkOutDate)
    return outDate.diff(inDate, "days" )
  };  

  return(
  <div>
    <table class="table table-bordered">
    <thead>
        <tr className="table-header">
          <th scope="col">ID</th>
          <th scope="col">Title</th>
          <th scope="col">First Name</th>
          <th scope="col">Surname</th>
          <th scope="col">Email</th>
          <th scope="col">Room-ID</th>
          <th scope="col">Check-In Date</th>
          <th scope="col">Check-Out Date</th>
          <th scope="col">Total Number of Nights</th>
          <th>Visit Customer Profile</th>
        </tr>
      </thead>
      <tbody>

        {results.map((info, index) => (
          <tr className = {rowClass? "grey-class": ""} onClick={ToggleRow}>
            <td key = "index">{info.id}</td>
            <td key = "index">{info.title}</td>
            <td key = "index">{info.firstName}</td>
            <td key = "index">{info.surname}</td>
            <td key = "index">{info.email}</td>
            <td key = "index">{info.roomId}</td>
            <td key = "index">{info.checkInDate}</td>
            <td key = "index">{info.checkOutDate}</td>
            <td key = "index"> {dateDiffs(info.checkInDate, info.checkOutDate)}</td>
            <button>Show Profile</button>
          </tr>  

          ))
        }

      </tbody>  
    </table>
    <CustomerProfile />
  </div>  


  )

}
export default SearchResults;

