import React from 'react'
import FakeBookings from './data/fakeBookings.json'
const NumberOfBookedNights = () => {
  const inDate = FakeBookings.map((date) =>(date.checkInDate))
  const outDate = FakeBookings.map((date)=>{
    return date.checkOutDate
  })
  const DateOut = new date(outDate)
  const DateIn = new date(inDate)
  const DifferenceInTime = DateOut.getTime() - DateIn.getTime();
  const DifferenceInDays = DifferenceInTime / (1000 * 3600 * 24);
  return <div>
    {DifferenceInDays}
  </div>
}
export default NumberOfBookedNights;
