import React, {useState} from "react";
import RestaurantButton from './RestaurantButton';

const Restaurant = () => {
  const orderOne = ()=>{
    const [orders, setOrders] = useState(0);
    return(
      setOrders(orders + 1)
    )
} 

  return (
    <div>
      <h3>Restaurant Orders</h3>
      <ul>
        <li>
          Pizzas: {orders} <RestaurantButton /> 
        </li>
      </ul>
    </div>
  );
};


export default Restaurant;
