import React, { useState } from 'react';
import moment from 'moment'
const TableRow = ({booking}) => {

  const [rowClass, setRowClass] = useState(false)
  const ToggleRow = () => {
    setRowClass(!rowClass)
  } 
  const dateDiffs = (checkInDate, checkOutDate) => {
    const inDate = moment(checkInDate)
    const outDate = moment(checkOutDate)
    return outDate.diff(inDate, "days" )
  }; 
  const {
    id,
    title, 
    firstName,
    surname,
    email,
    roomId,
    checkInDate,
    checkOutDate,
    showProfile
  } = booking
  return (
    <tr className = {rowClass? "grey-class": ""} onClick={ToggleRow}>
            <td key = "index">{id}</td>
            <td key = "index">{title}</td>
            <td key = "index">{firstName}</td>
            <td key = "index">{surname}</td>
            <td key = "index">{email}</td>
            <td key = "index">{roomId}</td>
            <td key = "index">{checkInDate}</td>
            <td key = "index">{checkOutDate}</td>
            <td key = "index"> {dateDiffs({checkInDate}, {checkOutDate})}</td>
            <button >{showProfile}</button>
          </tr>
  ) 
}
export default TableRow
